+ul.locale strong, span.value {
+    font-weight:normal;
+    color:#888;
+    font-size:.7em;
+}
+
+ul.locale li a {
+    font-size:1.2em;
+    display:block;
+    padding-top:.2em;
+}
+
+ul.locale li.total div.progress {
