+    background-color:#f7f2d0;
+    font-size:1.3em !important;
+    color:#ccc !important;
+    margin-bottom:1em;
+    min-height:9.5em;
+}
+
+li.total .value {
+    color:#444;
+    font-size:.8em;
+}
+
+li.total strong {
+    display:block;
+    color:black;
+    font-weight:bold;
+}
+
+li.total span.value {
+    position:relative;
