+	protected static String printPad4(int value) {
+		char[] result = new char[5];
+		int count = 0;
+
+		if (value < 0) {
+			result[count++] = '-';
+			value = -value;
+		}
+
+		String str = Integer.toString(value);
+
+		if (value < 10) {
+			result[count++] = '0';
+			result[count++] = '0';
+			result[count++] = '0';
+			result[count++] = str.charAt(0);
+		} else if (value < 100) {
+			result[count++] = '0';
