+			result[count++] = str.charAt(0);
+			result[count++] = str.charAt(1);
+		} else if (value < 1000) {
+			result[count++] = '0';
+			result[count++] = str.charAt(0);
+			result[count++] = str.charAt(1);
+			result[count++] = str.charAt(2);
+		} else {
+			result[count++] = str.charAt(0);
+			result[count++] = str.charAt(1);
+			result[count++] = str.charAt(2);
+			result[count++] = str.charAt(3);
+		}
+		return new String(result, 0, count);
+	}
